mapper.select:
SELECT INDIVIDUAL_NO, PROFILE_NO, INDIVIDUAL_ID, INDIVIDUAL_TITLE, INDIVIDUAL_CONTENT, INDIVIDUAL_MDATE, INDIVIDUAL_REGDATE
FROM INDIVIDUAL
WHERE INDIVIDUAL_ID = #{individual_id}
AND SUBSTR(INDIVIDUAL_MDATE, 1, 8) = #{yyyyMMdd}
ORDER BY INDIVIDUAL_NO DESC

mapper.update:
UPDATE INDIVIDUAL
SET INDIVIDUAL_TITLE = #{individual_title}, INDIVIDUAL_CONTENT = #{individual_content}
WHERE INDIVIDUAL_NO = #{individual_no}
